-- Ubicaciones
CREATE TABLE Ubicaciones (
    id_ubicacion SERIAL PRIMARY KEY,
    nombre_ubicacion VARCHAR(255) NOT NULL,
    id_padre INTEGER REFERENCES Ubicaciones(id_ubicacion)
);

-- Generos
CREATE TABLE Generos (
    id_genero SERIAL PRIMARY KEY,
    nombre_genero VARCHAR(100) UNIQUE NOT NULL,
    descripcion_genero TEXT
);

-- Peliculas
CREATE TABLE Peliculas (
    id_pelicula SERIAL PRIMARY KEY,
    nombre_pelicula VARCHAR(255) NOT NULL,
    sinopsis_pelicula TEXT,
    id_genero INTEGER REFERENCES Generos(id_genero),
    duracion_pelicula Integer NOT NULL,
    idioma VARCHAR(50),
    thumbnail VARCHAR(255)
);

-- Cines
CREATE TABLE Cines (
    id_cine SERIAL PRIMARY KEY,
    id_ubicacion INTEGER REFERENCES Ubicaciones(id_ubicacion),
    nombre_cine VARCHAR(255) NOT NULL,
    telefono_cine VARCHAR(50)
);

-- Tipos_salas
CREATE TABLE Tipos_salas (
    id_tipo_sala SERIAL PRIMARY KEY,
    nombre VARCHAR(100) NOT NULL,
    descripcion VARCHAR(255)
);

-- Salas
CREATE TABLE Salas (
    id_sala SERIAL PRIMARY KEY,
    nombre_sala VARCHAR(100) NOT NULL,
    id_tipo_sala INTEGER REFERENCES Tipos_salas(id_tipo_sala),
    sala_capacidad INTEGER NOT NULL,
    id_cine INTEGER REFERENCES Cines(id_cine)
);

-- Funciones
CREATE TABLE Funciones (
    id_funcion SERIAL PRIMARY KEY,
    id_sala INTEGER REFERENCES Salas(id_sala),
    id_pelicula INTEGER REFERENCES Peliculas(id_pelicula),
    fecha_funcion DATE NOT NULL,
    hora_inicio_funcion TIME NOT NULL
);


-- Cargos
CREATE TABLE Cargos (
    id_cargo SERIAL PRIMARY KEY,
    nombre_cargo VARCHAR(100) UNIQUE NOT NULL,
    descripcion_cargo TEXT
);

-- Usuarios
CREATE TABLE Usuarios (
    id_usuario SERIAL PRIMARY KEY,
    nombre_usuario VARCHAR(50) NOT NULL,
    contrasena_usuario VARCHAR(255) NOT NULL,
    fecha_creacion DATE NOT NULL,
    apellido_usuario VARCHAR(100) NOT NULL,
    fecha_nacimiento_usuario DATE,
    id_cine INTEGER REFERENCES Cines(id_cine),
    id_cargo INTEGER REFERENCES Cargos(id_cargo),
    email_usuario VARCHAR(255) UNIQUE NOT NULL
);

-- Reservaciones
CREATE TABLE Reservaciones (
    id_reservacion SERIAL PRIMARY KEY,
    id_funcion INTEGER REFERENCES Funciones(id_funcion),
    id_usuario INTEGER REFERENCES Usuarios(id_usuario),
    cantidad_boletas INTEGER NOT NULL,
    fecha_reserva DATE NOT NULL
);

-- Sillas
CREATE TABLE Sillas (
    id_silla SERIAL PRIMARY KEY,
    id_sala INTEGER REFERENCES Salas(id_sala),
    nombre_silla VARCHAR(50) NOT NULL
);

-- Sillas_reservadas
CREATE TABLE Sillas_reservadas (
    id_silla_reservada SERIAL PRIMARY KEY,
    id_reservacion INTEGER REFERENCES Reservaciones(id_reservacion),
    id_silla INTEGER REFERENCES Sillas(id_silla)
);

-- Metodos_de_pago
CREATE TABLE Metodos_de_pago (
    id_metodo_pago SERIAL PRIMARY KEY,
    nombre_pago VARCHAR(100) NOT NULL,
    descripcion_pago TEXT
);

-- Pagos
CREATE TABLE Pagos (
    id_pago SERIAL PRIMARY KEY,
    id_reserva INTEGER REFERENCES Reservaciones(id_reservacion),
    id_metodo_pago INTEGER REFERENCES Metodos_de_pago(id_metodo_pago),
    fecha_pago DATE NOT NULL,
    valor_pago NUMERIC(10, 2) NOT NULL
);

-- Comidas
CREATE TABLE Comidas (
  id_comida serial PRIMARY KEY,
  nombre_comida varchar(255),
  precio_comida money
); 

-- Menu_cine
CREATE TABLE Menu_cine (
    id_menu_cine SERIAL PRIMARY KEY,
    id_comida INTEGER REFERENCES Comidas(id_comida)
        ON DELETE RESTRICT
        ON UPDATE CASCADE,
    id_cine INTEGER REFERENCES Cines(id_cine),
    precio money
);





















